    public void levelup(int skillId) {
        LevelUpData.Skill skill = getSkillById(skillId);
        if (skill == null) return; // invalid skill

        int level = playerLevel[skill.getId()];

        getPA().sendFrame164(skill.getFrameId());
        stillgfx(199, absX, absY);

        getPA().sendFrame126("Congratulations, you just advanced a " + skill.getDisplayName() + " level!", 4268);
        getPA().sendFrame126("Your " + skill.getDisplayName() + " level is now " + level + " .", 4269);
        sendMessage("Congratulations, you just advanced a " + skill.getDisplayName() + " level.");
        NpcDialogueSend = true;

        if (level >= 99) {
            if (!playerHasItemEverywhere(skill.getCape()) && !playerHasItemEverywhere(skill.getCapeTrim()) && !playerHasItemEverywhere(skill.getHood())) {
                addItemToBank(skill.getCape(), 1);
                addItemToBank(skill.getCapeTrim(), 1);
                addItemToBank(skill.getHood(), 1);
                sendMessage("Congratulations, you just received the " + skill.getDisplayName() + " skill cape and hood!");
                PlayerHandler.messageToAll = playerName + " has just gotten " + level + " " + skill.getDisplayName() + "!";
            }
        }
        }

    private LevelUpData.Skill getSkillById(int id) {
        for (LevelUpData.Skill s : LevelUpData.Skill.values()) {
            if (s.getId() == id) return s;
        }
        return null; // invalid id
    }

    public void attackPlayersWithin(int gfx, int maxDamage, int range) {
        for (Player p : PlayerHandler.players) {
            if (p != null) {
                client person = (client) p;

                if ((person.playerName != null || person.playerName != "null")) {
                    if (person.distanceToPoint(absX, absY) <= range
                            && person.index != index && !person.nonWild()) {
                        int damage = misc.random(maxDamage);

                        person.stillgfx(gfx, person.absY, person.absX);
                        if (person.playerLevel[3] - hitDiff < 0) {
                            damage = person.playerLevel[3];
                        }
                        person.hitDiff = damage;
                        person.KillerId = index;
                        person.setUpdateRequired(true);
                        person.hitUpdateRequired = true;
                    }
                }
            }
        }
    }

    public void attackNPCSWithin(int gfx, int maxDamage, int range) {
        for (int i = 0; i < NPCHandler.maxNPCs; i++) {
            NPC npc = NPCHandler.npcs[i];
            if (npc != null &&
                    distanceToPoint(npc.absX, npc.absY) <= range &&
                    !npc.IsDead &&
                    npc.HP != 1000 &&
                    npcId != 2475 &&
                    npcId != 2259) {

                int damage = misc.random(maxDamage);
                stillgfx(gfx, npc.absY, npc.absX);
                if (npc.HP - hitDiff < 0) {
                    damage = npc.HP;
                }
                hitDiff = damage;
                npc.StartKilling = index;
                npc.RandomWalk = false;
                npc.IsUnderAttack = true;
                npc.hitDiff = damage;
                npc.setUpdateRequired(true);
                npc.hitUpdateRequired = true;
            }
        }

    }

