    @Override
    public void destruct() {
        if (mySock == null) {
            return; // Already shut down or never connected
        }

        try {
            if(hasActiveFamiliar()){
                Summoning.cleanup(this);
            }
            if (hasNpc)
                getPets().quickPickup(this, summonId);
            disconnected = true;
            ConnectionList.getInstance().removeConnection(mySock.getInetAddress());

            misc.println("ClientHandler: Client " + playerName + " disconnected.");

            if (clan != null) {
                clan.removeMember(this);
                clan = null;
            }

            try {
                if (in != null) in.close();
            } catch (Exception ignored) {}
            try {
                if (out != null) out.close();
            } catch (Exception ignored) {}
            try {
                mySock.close();
            } catch (Exception ignored) {}

            server.panel.removeEntity(playerName);
            server.getEventHandler().stop(this);
            CycleEventHandler.getSingleton().stopEvents(this);
            savechar();
            savemoreinfo();
            mySock = null;
            in = null;
            out = null;
            inStream = null;
            buffer = null;
            isActive = false;

            synchronized (this) {
                notifyAll();
            }

        } catch (Exception e) {
            System.err.println(" Unexpected error in destruct() for " + playerName);
            e.printStackTrace();
        }

        super.destruct();
    }

