    // Writes any data in outStream to the relaying buffer
    public void flushOutStream() {
        if (disconnected || getOutStream().currentOffset == 0) {
            return;
        }

        synchronized (this) {
            int availableSpace = (readPtr + bufferSize - writePtr - 1) % bufferSize;
            int bytesToWrite = Math.min(getOutStream().currentOffset, availableSpace);

            if (bytesToWrite == 0) {
                shutdownError("Buffer overflow.");
                disconnected = true;
                return;
            }

            // If the write pointer would wrap around, split the write into two operations
            int firstPartSize = Math.min(bytesToWrite, bufferSize - writePtr);
            System.arraycopy(getOutStream().buffer, 0, buffer, writePtr, firstPartSize);

            if (bytesToWrite > firstPartSize) {
                int secondPartSize = bytesToWrite - firstPartSize;
                System.arraycopy(getOutStream().buffer, firstPartSize, buffer, 0, secondPartSize);
                writePtr = secondPartSize;
            } else {
                writePtr = (writePtr + firstPartSize) % bufferSize;
            }

            getOutStream().currentOffset = 0;
            notify(); // Notify any waiting threads that data is available
        }
    }


    private void directFlushOutStream() {
        stream outStream = getOutStream();
        if (out == null || outStream == null) {
            return;
        }

        try {
            int len = outStream.currentOffset;

            // Validate before writing
            if (len < 0 || len > outStream.buffer.length) {
                outStream.currentOffset = 0;
                return;
            }

            out.write(outStream.buffer, 0, len);
            out.flush(); // important: ensures actual send

        } catch (IOException io) {
            disconnected = true;
            misc.println(" Flush failed (" + playerName + "): " + io.getMessage());

        } catch (Throwable t) {
            disconnected = true;
            misc.println(" Unexpected flush error (" + playerName + "): " + t.getMessage());

        } finally {
            // Always reset regardless of success/failure
            outStream.currentOffset = 0;
        }
    }


    private static int getTrailingZeroBits(byte[] bigNumber) {
        int bits = 0;
        for (byte var4 : bigNumber) {
            int n = getTrailingZeroBits(var4);
            bits += n;
            if (n != 8) {
                break;
            }
        }
        return bits;
    }

    private static int getTrailingZeroBits(byte v) {
        if (v == 0) {
            return 8;
        }
        int bits = 0;
        int t = v & 255;
        while ((t & 128) == 0) {
            bits++;
            t <<= 1;
        }
        return bits;
    }

    /**
     * Generates a random seed for 'proof of work'
     * @param n The number of random characters to generate
     *          within our seed
     * @return A newly created seed
     */
    private static String generateSeed(int n) {

        // chose a Character random from this String
        String AlphaNumericString = "ABCDEFGHIJKLMNOPQRSTUVWXYZ"
                + "0123456789"
                + "abcdefghijklmnopqrstuvxyz";

        // create StringBuffer size of AlphaNumericString
        StringBuilder sb = new StringBuilder(n);

        for (int i = 0; i < n; i++) {

            // generate a random number between
            // 0 to AlphaNumericString variable length
            int index
                    = (int) (AlphaNumericString.length()
                    * Math.random());

            // add Character one by one in end of sb
            sb.append(AlphaNumericString
                    .charAt(index));
        }

        return sb.toString();
    }
    private boolean fillInStream(int required) {
        // --- Validate packet length ----------------------------------------------
        if (required < 0 || required > inStream.buffer.length) {
            return false;
        }

        final long deadline = System.currentTimeMillis() + 5000; // 5s max window
        int offset = 0;

        try {
            while (offset < required) {

                // Hard timeout protects from hanging or slow-flood attacks
                if (System.currentTimeMillis() > deadline) {
                    return false;
                }

                int remaining = required - offset;

                int read = in.read(inStream.buffer, offset, remaining);

                if (read < 0) {
                    return false; // disconnected
                }

                if (read == 0) {
                    // Avoid CPU spike under heavy flooding
                    try { Thread.sleep(1); } catch (InterruptedException ignored) {}
                    continue;
                }

                offset += read;
            }

            // Reset the logical offset once fully read
            inStream.currentOffset = 0;
            return true;

        } catch (Exception e) {
            return false; // any I/O error = fail
        }
    }

