    public int readSave() {
        if (PlayerHandler.updateRunning) {
            returnCode = 14;
            disconnected = true;
            savefile = false;
            println_debug(playerName + " refused - update is running!");
            return 3;
        }

        if (PlayerHandler.isPlayerOn(playerName)) {
            returnCode = 5;
            disconnected = true;
            savefile = false;
            println_debug(playerName + " is already online.");
            return 3;
        }

        // Attempt to load the game
        int loadResult = loadGame(playerName, playerPass, macAddress);

        if (loadResult == 2) { // Wrong password or invalid player
            println_debug("Wrong password for " + playerName);
            returnCode = 3;
            disconnected = true;
            savefile = false;
            return 3;
        } else if (loadResult == 3) { // New account required
            File file = new File("./Data/characters/" + playerName + ".json");

            // Safety: if file exists, do not overwrite
            if (file.exists()) {
                println_debug("Load failed for existing account: " + playerName);
                returnCode = 3;
                disconnected = true;
                savefile = false;
                return 3;
            }

            // Create new account
            returnCode = 2;
            disconnected = false;
            savefile = true;

            boolean found = false;
            for (int i = 0; i < server.MaxConnections; i++) {
                if (server.Connections[i] == connectedFrom) {
                    server.ConnectionCount[i]++;
                    found = true;
                    break;
                }
            }
            if (!found) {
                for (int i = 0; i < server.MaxConnections; i++) {
                    if (server.Connections[i] == null) {
                        server.Connections[i] = connectedFrom;
                        server.ConnectionCount[i] = 1;
                        break;
                    }
                }
            }
        } else if (loadResult == 1) { // Normal successful load
            returnCode = 2;
            disconnected = false;
            savefile = true;
        } else { // Fail-safe
            returnCode = 3;
            disconnected = true;
            savefile = false;
            return 3;
        }

        return 1;
    }


