    public boolean playerHasItem(int itemID, int amt) {
        itemID++;
        int found = 0;
        for (int i = 0; i < playerItems.length; i++) {
            if (playerItems[i] == itemID) {
                if (playerItemsN[i] >= amt) {
                    return true;
                } else {
                    found++;
                }
            }
        }
        if (found >= amt) {
            return true;
        }
        return false;
    }
    public boolean addItem(int item, int amount) {
        if (amount < 1) {
            return false;
        }
        if (item <= 0) {
            return false;
        }
        if ((((freeSlots() >= 1) || playerHasItem(item, 1)) && ItemCacheDefinition.forID(item).isStackable())
                || ((freeSlots() > 0) && !ItemCacheDefinition.forID(item).isStackable())) {
            for (int i = 0; i < playerItems.length; i++) {
                if (playerItems[i] == (item + 1) && ItemCacheDefinition.forID(item).isStackable()
                        && playerItems[i] > 0) {
                    playerItems[i] = (item + 1);
                    if ((playerItemsN[i] + amount) < maxItemAmount && (playerItemsN[i] + amount) > -1) {
                        playerItemsN[i] += amount;
                    } else {
                        playerItemsN[i] = maxItemAmount;
                    }
                    if (getOutStream() != null) {
                        getOutStream().createFrameVarSizeWord(34);
                        getOutStream().writeWord(3214);
                        getOutStream().writeByte(i);
                        getOutStream().writeWord(playerItems[i]);
                        if (playerItemsN[i] > 254) {
                            getOutStream().writeByte(255);
                            getOutStream().writeDWord(playerItemsN[i]);
                        } else {
                            getOutStream().writeByte(playerItemsN[i]); // amount
                        }
                        getOutStream().endFrameVarSizeWord();
                        flushOutStream();
                    }
                    i = 30;
                    return true;
                }
            }
            for (int i = 0; i < playerItems.length; i++) {
                if (playerItems[i] <= 0) {
                    playerItems[i] = item + 1;
                    if (amount < maxItemAmount && amount > -1) {
                        playerItemsN[i] = 1;
                        if (amount > 1) {
                            addItem(item, amount - 1);
                            return true;
                        }
                    } else {
                        playerItemsN[i] = maxItemAmount;
                    }
                    getPA().resetItems(3214);
                    i = 30;
                    return true;
                }
            }
            return false;
        } else {
            getPA().resetItems(3214);
            sendMessage("Not enough space in your inventory.");
            return false;
        }
    }

