    public String getPrestigeTitle(int prestigeLevel) {
        // Define color codes for each 10-level tier
        String[] colors = {
                "FFFFFF", // 1-10: white
                "00FF00", // 11-20: green
                "0000FF", // 21-30: blue
                "FFFF00", // 31-40: yellow
                "FF00FF", // 41-50: magenta
                "00FFFF", // 51-60: cyan
                "FFA500", // 61-70: orange
                "800080", // 71-80: purple
                "FF0000", // 81-90: red
                "7851A9"  // 91-100: custom dark purple
        };

        // Determine tier index (0-9)
        int tierIndex = (prestigeLevel - 1) / 10;
        if (tierIndex >= colors.length) tierIndex = colors.length - 1;

        // Return formatted title with color
        return "<col=" + colors[tierIndex] + "><shad=000000>[Prestige " + prestigeLevel + "] </col></shad>";
    }

    public boolean packetSending() { // Ringer24: this is used for sending packets
        return packetProcess();
    }

    private boolean packetProcess() {
        if (disconnected || in == null) return false;

        try {
            // Wait for available data
            if (in.available() <= 0) return false;

            // Step 1: Read packet type if unknown
            if (packetType == -1) {
                int opcode = in.read();
                if (opcode == -1) {
                    disconnected = true;
                    return false;
                }
                // Validate opcode range
                if (opcode < 0 || opcode > 256) {
                    // Optionally log malformed packets
                    // logUnknownOpcode(opcode);
                    resetPacket();
                    return false;
                }
                // Decrypt with ISAAC if applicable
                if (inStreamDecryption != null) {
                    opcode =  ((opcode - inStreamDecryption.getNextIntKey()) & 0xFF);
                }


                packetType = opcode;
                packetSize = packetSizes.getPacketSize(packetType);

                // OPCODE_OUT_OF_RANGE_SIZE check (i.e., if packet is invalid)
                if (packetSize == packetSizes.OPCODE_OUT_OF_RANGE_SIZE) {
                    // logUnknownOpcode(packetType);
                    resetPacket();
                    return false;
                }
            }

            // Step 2: Handle variable-length packets (size = -1)
            if (packetSize == -1) {
                if (in.available() < 1) return false;
                packetSize = in.read();
                if (packetSize == -1) {
                    disconnected = true;
                    return false;
                }
                packetSize &= 0xFF;
            }

            // Step 3: Ensure full packet payload is available
            if (in.available() < packetSize) return false;

            // Step 4: Read and parse packet data
            fillInStream(packetSize);
            parseIncomingPackets();
            timeOutCounter = 0;

        } catch (Exception e) {
            System.err.println("[GHR Packet Error] Fatal while reading packet. Disconnecting...");
            e.printStackTrace();
            disconnected = true;

        } finally {
            // Always reset state
            resetPacket();
        }

        return true;
    }


    // Resets packetType and packetSize for next packet processing
    private void resetPacket() {
        packetType = -1;
        packetSize = -1;
    }

    public boolean pickUpItem(int itemId, int amount) {
        if (amount < 1) amount = 1;
        if (!ItemCacheDefinition.forID(itemId).isStackable()) amount = 1;

        // Try to stack it first
        if (ItemCacheDefinition.forID(itemId).isStackable()) {
            for (int i = 0; i < playerItems.length; i++) {
                if (playerItems[i] == itemId + 1) {
                    if ((long) playerItemsN[i] + amount > maxItemAmount) {
                        sendMessage("You can't carry that many.");
                        return false;
                    }
                    playerItemsN[i] += amount;
                    refreshInventorySlot(i);
                    return true;
                }
            }
        }

        // Find free slot for new item
        int freeSlot = getFreeInventorySlot();
        if (freeSlot == -1) {
            sendMessage("Your inventory is full.");
            return false;
        }

        if ((long) amount > maxItemAmount) {
            sendMessage("That amount is too large.");
            return false;
        }

        playerItems[freeSlot] = itemId + 1;
        playerItemsN[freeSlot] = amount;
        refreshInventorySlot(freeSlot);
        return true;
    }
    private int getFreeInventorySlot() {
        for (int i = 0; i < playerItems.length; i++) {
            if (playerItems[i] <= 0) return i;
        }
        return -1;
    }
