                actionAmount++;
                actionTimer = 4;
                OriginalShield = playerEquipment[playerShield];
                OriginalWeapon = playerEquipment[playerWeapon];
                playerEquipment[playerShield] = useitems[0];
                playerEquipment[playerWeapon] = useitems[1];
